import logo from './logo.svg';
import React, { Component } from 'react'
import './App.css';
import "~slick-carousel/slick/slick.css"; 
import "~slick-carousel/slick/slick-theme.css";

class ItemProjectEntrepreneur extends Component {
  /*constructor(props) {
    super(props);

    this.state = {
      profil: "visiteur"
    }
  }*/

    render() {
         {
            var image = this.props.data.image,
                    title = this.props.data.title,
                    subtitle = this.props.data.subtitle;
            return (
                <figure className="snip1584">
                    <img src={image} />
                    <figcaption>
                        <h3>{title}</h3>
                        <h5>{subtitle}</h5>
                    </figcaption><a href="#"></a>
                </figure>
            )
        }
    }
  }

export default ItemProjectEntrepreneur;






































var Article = React.createClass({
	render: function() {
		var image = this.props.data.image,
				title = this.props.data.title,
				subtitle = this.props.data.subtitle;
		return (
			<figure className="snip1584">
				<img src={image} />
				<figcaption>
					<h3>{title}</h3>
					<h5>{subtitle}</h5>
				</figcaption><a href="#"></a>
			</figure>
		)
	}
});
var News = React.createClass({
	render: function() {
		var data = this.props.data;
		var newsTemplate;
		var settings = {
    	dots: true,
			infinite: true,
  		slidesToShow: 3,
  		slidesToScroll: 1,
    }
		if (data.length > 0) {
			newsTemplate = data.map(function(item, index) {
				return (
						<div key={index}>
							<Article data={item} />
						</div>
				)
			})
		} else {
			newsTemplate = <p>Please add some cards</p>
		}
		return (
			<div className='news'>
				<Slider {...settings}>{newsTemplate}</Slider>
				<strong className={'news__count ' + (data.length > 0 ? '':'none') }>
					Total cards: {data.length}
				</strong>
			</div>
		);
	}
});
var App = React.createClass({
	render: function() {
		
		return (
			<div className="App">
				<h3>Cards</h3>
				<News data={cards} />
			</div>
		);
	}
});
