import logo from './logo.svg';
import React, { Component } from 'react'
import SignInUp from './SignInUp'
import Headers from './Headers'
import Body from './Body'
import Footers from './Footers'
import './App.css';
import axios from "axios";

var init;
class home extends Component {
  constructor(props) {
    super(props);

    this.state = {
      profil: "visiteur",
      dataInit: []
    }
    this.allResults = this.allResults.bind(this);
  }
  
    handleCardClick = index => {
      console.log(index, this);
    }
    handleEntrepreneurClick = index => {
      console.log(index, this);
    }
    handleSelectedChange(event) {
      console.log(event.target.value);
    }
    allResults() {
      var b = document.querySelector("header");
      b.setAttribute("padding-top", "0%");
      axios.get ("http://localhost:5000/").then(res => {
        this.setState({dataInit: res.data.data}) 
      });
    }
    allEvents(){
      var slideIndex = 1;
      showSlides(slideIndex);

      function plusSlides(n) {
        showSlides(slideIndex += n);
      }

      function currentSlide(n) {
        showSlides(slideIndex = n);
        }

      function showSlides(n) {
        var i;
        var slides = document.getElementsByClassName("mySlides");
        var dots = document.getElementsByClassName("dot");
        if (n > slides.length) {slideIndex = 1}    
        if (n < 1) {slideIndex = slides.length}
        for (i = 0; i < slides.length; i++) {
            slides[i].style.display = "none";  
        }
        for (i = 0; i < dots.length; i++) {
            dots[i].className = dots[i].className.replace(" active", "");
        }
        slides[slideIndex-1].style.display = "block";  
        dots[slideIndex-1].className += " active";
      }
    }
    
    render() {
      const {profil, dataInit} = this.state
      return (
        <div className="App">
          <Headers profil={profil} onClickLogin={this.onLoginClick} onClickSignUp={this.onSignUpClick} where={"home"}/>
          <Body profil={profil} project={dataInit} onClickAllResults={this.allResults} onChangeLiked={this.handleSelectedChange} onClick={this.handleCardClick} onClick1={this.handleEntrepreneurClick} test={this.allEvents}/>
          <Footers profil={profil}/>
        </div>
      )
    }
  }

export default home;
