import logo from './logo.svg';
import React, { Component } from 'react'
import Headers from './Headers'
import SignInUp from './SignInUp'
import Body from './Body'
import './App.css';
const FAKE_HOF = [
    { title: 'Mountain View', person: 'Check out all of these gorgeous mountain trips with beautiful views of, you guessed it, the mountains', id: '1517021897933-0e0319cfbc28' }, 
    { title: 'To The Beach', person: 'Plan your next beach trip with these fabulous destinations', id: '1533903345306-15d1c30952de' }, 
    { title: 'Desert Destinations', person: 'It\'s the desert you\'ve always dreamed of', id: '1545243424-0ce743321e11' },
    { title: 'To The Beach', person: 'Plan your next beach trip with these fabulous destinations', id: '1533903345306-15d1c30952de' },
    { title: 'Desert Destinations', person: 'It\'s the desert you\'ve always dreamed of', id: '1545243424-0ce743321e11' },
    { title: 'Mountain View', person: 'Check out all of these gorgeous mountain trips with beautiful views of, you guessed it, the mountains', id: '1517021897933-0e0319cfbc28' }, 
    { title: 'To The Beach', person: 'Plan your next beach trip with these fabulous destinations', id: '1533903345306-15d1c30952de' }, 
    { title: 'Desert Destinations', person: 'It\'s the desert you\'ve always dreamed of', id: '1545243424-0ce743321e11' },
    { title: 'Desert Destinations', person: 'It\'s the desert you\'ve always dreamed of', id: '1545243424-0ce743321e11' },
    { title: 'To The Beach', person: 'Plan your next beach trip with these fabulous destinations', id: '1533903345306-15d1c30952de' }, 
    { title: 'Explore The Galaxy', person: 'Seriously, straight up, just blast off into outer space today', id: '1531306728370-e2ebd9d7bb99' },
    { title: 'To The Beach', person: 'Plan your next beach trip with these fabulous destinations', id: '1533903345306-15d1c30952de' },
  ]
  
  const switchers = [...document.querySelectorAll('.switcher')]

  switchers.forEach(item => {
    item.addEventListener('click', function() {
      switchers.forEach(item => item.parentElement.classList.remove('is-active'))
      this.parentElement.classList.add('is-active')
    })
  })
class home extends Component {
  constructor(props) {
    super(props);
    this.state = {
      profil: "visiteur",
    }
    this.handleChange = this.handleSelectedChange.bind(this);
  }

    handleCardClick = index => {
      console.log(index, this);
    }
    handleEntrepreneurClick = index => {
      console.log(index, this);
    }
    handleSelectedChange(event) {
      console.log(event.target.value);
    }
    handleChangePanelRight(event) {
      document.getElementById('container').classList.add("right-panel-active");
      this.setState({value: event.target.value});
    }
    handleChangePanelLeft(event) {
      document.getElementById('container').classList.remove("right-panel-active");
      this.setState({value: event.target.value});
    }
    render() {
      const {profil} = this.state
      return (
        <div className="App">
          <Headers profil={profil}/>
          {/*<SignInUp changePanelIn={this.handleChangePanelRight} changePanelUp={this.handleChangePanelLeft}/>*/}
          <Body profil={profil} project={FAKE_HOF} onChangeLiked={this.handleSelectedChange} onClick={this.handleCardClick} onClick1={this.handleEntrepreneurClick}/>
        </div>
      )
    }
  }

export default home;
