import PropTypes from 'prop-types'
import React, { Component } from 'react'
import './Body.css'

handleCardClick = index => {
  console.log(index, this)
    const { currentPair } = this.state

  if (currentPair.length === 2) {
    return
  }
  if (currentPair.length === 0) {
    this.setState({ currentPair: [index] })
    return
  }
    this.handleNewPairClosedBy(index)
}


const ProjectBody = ({profil, project, index}) => (
    <div className="projectBodyContainer">
      {profil === "visiteur" ? 
          <div className="tabProject">
              <div className={`project ${project.title} ${project.person}`} onClick={() => onClick(index)}>
                <span className="symbol"  id={project.id}>
                </span>
              </div>
          </div>
        :
        <div>En cours</div>
      }
    </div>
  )




export default ProjectBody
