import React, { Component } from 'react'
import axios from "axios";

class Entrepreneur extends Component {
  constructor(props) {
    super(props);
    this.state = {
      profil: "visiteur"
    }
    this.handleChange = this.handleChange.bind(this);
  }
  handleChange(event) {
    this.setState({
      [event.target.name]: event.target.value
    });
  }
  handleSubmit(event) {
    const { email, password } = this.state;
    axios.post("http://localhost:3000/login",{
            email: email,
            password: password
        },
        { withCredentials: true }
      )
      .then(response => {
        if(!response.data.err){
          if (response.data) {
            this.setState({ redirect: true });
            console.log(response);
            //this.props.handleSuccessfulAuth(response.data);
          }
        }else{
          alert(response.data.err);
        }

      })
      .catch(error => {
        console.log("submit error", error);
      });
    event.preventDefault();
  }
  fileSelectedhandler(event) {
    console.log(event.target.files[0]);
  }

  render (){
    return(
      <div id="idInterfaceEntrepreneur" className="form-wrapper">
      <form className="form"  onSubmit={this.handleSubmit} action="entrepreneur" method="POST">
        <fieldset className="interface-entrepreneur">
          <div className="input-block">
            <label>Photo entrepreneur</label>
            <input id="entrepreneur-picture" type="file" name="entrepreneur-picture" onChange={this.fileSelectedhandler} required/>
          </div>
          <div className="input-block">
            <label>Photo principale du projet</label>
            <input id="project-picture" type="file" name="project-picture" onChange={this.fileSelectedhandler} required/>
          </div>
          <div className="input-block">
            <label>Photos du projet</label>
            <input id="project-pictures" type="file" name="project-pictures" onChange={this.fileSelectedhandler} required/>
          </div>
          <div className="input-block">
            <label>Video de présentation du projet</label>
            <input id="project-video" type="file" name="project-video" onChange={this.fileSelectedhandler} required/>
          </div>
          <div className="input-block">
            <label>Descriptif du projet</label>
            <textarea id="project-description" type="email" name="project-description" onChange={this.handleChange} required/>
          </div>
          <div className="input-block">
            <label>Timeline/planning</label>
            <input id="project-planning" type="email" name="project-planning" onChange={this.handleChange} required/>
          </div>
          <div className="input-block">
            <label>Besoins/Financement</label>
            <input id="project-funding" type="email" name="project-funding" onChange={this.handleChange} required/>
          </div>
          <div className="input-block">
            <label>Réseaux sociaux de suivi du projet</label>
            <input id="project-social-media" type="email" name="project-social-media" onChange={this.handleChange} required/>
          </div>
        </fieldset>
        <button type="submit" className="btn-submit">
         Valider
        </button>
        <button type="submit" className="btn-submit">
         Annuler
        </button>
      </form>
    </div>
    )
 
  }
}




export default Entrepreneur
