import PropTypes from 'prop-types'
import './SignInUp.css'
import Headers from './Headers'
import axios from "axios";
import { Redirect  } from 'react-router-dom';
import React, { Component } from 'react'
/*const contextEntrepreneur = React.createContext([]);*/

class SignInUp extends Component {
  constructor(props) {
    super(props);
    this.state = {
      profil: "visiteur",
      firstName: "",
      lastName: "",
      email: "",
      password: "",
      confirmPwd: "",
      loginErrors: "",
      redirect: false,
      dataEntrepreneur: []
    }
    this.handleLogin = this.handleLogin.bind(this);
    this.handleSignUp = this.handleSignUp.bind(this);
    this.checkPassword = this.checkPassword.bind(this);
    this.handleChange = this.handleChange.bind(this);
  }
  handleChangePanelLogin(event) {
    document.getElementById("idPanelLogin").classList.add("is-active");
    document.getElementById("idPanelSignUp").classList.remove("is-active");
    document.getElementById("signup-firstname").value = "";
    document.getElementById("signup-lastname").value = "";
    document.getElementById("signup-email").value = "";
    document.getElementById("signup-password").value = "";
    document.getElementById("signup-password-confirm").value = "";
  }
  handleChangePanelSignUp(event) {
    document.getElementById('idPanelSignUp').classList.add("is-active");
    document.getElementById('idPanelLogin').classList.remove("is-active");
    document.getElementById("login-email").value = "";
    document.getElementById("login-password").value = "";
  }
  handleChange(event) {
    this.setState({
      [event.target.name]: event.target.value
    });
  }
  checkPassword(password,confirmPwd) {
    var decimal=  /^(?=.*\d)(?=.*[a-z])(?=.*[A-Z])(?=.*[^a-zA-Z0-9])(?!.*\s).{8,15}$/;
    if(!password.match(decimal)) { 
        alert("Password between 8 to 15 characters which contain at least one lowercase letter, one uppercase letter, one numeric digit, and one special character")
        return;
      }
      if(password !== confirmPwd){
        alert("Password doesn't match");
        return
      }
      return true;
  }
  togglePassword(event) {
    if(event.target.className === "fa fa-fw fa-eye field-icon toggle-password"){
      var pwd = document.getElementById("login-password");
      if (pwd.type === "password") {
        pwd.type = "text";
      } else {
        pwd.type = "password";
      }
    }else{
      var pwd = document.getElementById("signup-password");
      if (pwd.type === "password") {
        pwd.type = "text";
      } else {
        pwd.type = "password";
      }
    }

  };
  handleLogin(event) {
    const { email, password } = this.state;
    axios.post("http://localhost:3000/login",{
            email: email,
            password: password
        },
        { withCredentials: true }
      )
      .then(response => {
        if(!response.data.err){
          if (response.data) {
            this.setState({redirect: true});
            this.setState({dataEntrepreneur: response.data});
            console.log(response);
            //this.props.handleSuccessfulAuth(response.data);
          }
        }else{
          alert(response.data.err);
        }

      })
      .catch(error => {
        console.log("login error", error);
      });
    event.preventDefault();
  }
  handleSignUp(event) {
    const { firstName, lastName, email, password, confirmPwd } = this.state;
    if(this.checkPassword(password,confirmPwd)){
      axios.post("http://localhost:3000/register",{
        firstName: firstName,
        lastName: lastName,
        email: email,
        password: password
    },
    { withCredentials: true }
    )
    .then(response => {
      if(!response.data.err){
      if (response.data) {
        console.log(response);
        //this.props.handleSuccessfulAuth(response.data);
      }
    }else{
      alert(response.data.err);
    }
    })
    .catch(error => {
      console.log("login error", error);
    });
  }else{
    console.log("error password");
  }

    event.preventDefault();
  }

    render() {
      const {profil, redirect } = this.state
      if (redirect) {
        <contextEntrepreneur.Provider value={this.state.dataEntrepreneur}></contextEntrepreneur.Provider>
        return <Redirect to="/entrepreneur"/>;
      }
      return (
        <section className="forms-section" id="containerLogSign">
        <Headers profil={profil} onClickLogin={this.onLoginClick} onClickSignUp={this.onSignUpClick} where={"siu"}/>
        <h1 className="section-title"></h1>
        <div className="forms" >
          <div id="idPanelLogin" className="form-wrapper is-active">
            <button type="button"  className="switcher switcher-login" onClick={this.handleChangePanelLogin}>
              Login
              <span className="underline"></span>
            </button>
            <form className="form form-login"  onSubmit={this.handleLogin} method="POST"> {/*action=""*/}
              <fieldset>
                <legend>Please, enter your email and password for login.</legend>
                <div className="input-block">
                  <label>E-mail</label>
                  <input id="login-email" type="email" name="email" onChange={this.handleChange} required/>
                </div>
                <div className="input-block">
                  <label>Password</label>
                  <input id="login-password" type="password" name="password" onChange={this.handleChange} required/>
                  <span toggle="#password-field" name="login-password-view" className="fa fa-fw fa-eye field-icon toggle-password" onClick={this.togglePassword}></span>
                </div>
              </fieldset>
              <button type="submit" className="btn-login">
              Login
              {/*<Entrepreneur dataEntrepreneur={dataEntrepreneur}></Entrepreneur> <Link to="/entrepreneur">Login</Link>*/}
              </button>
            </form>
          </div>
          <div id="idPanelSignUp" className="form-wrapper">
            <button type="button"  className="switcher switcher-signup right-panel-active" onClick={this.handleChangePanelSignUp}>
              Sign Up
              <span className="underline"></span>
            </button>
            <form className="form form-signup"  onSubmit={this.handleSignUp} action="entrepreneur" method="POST">
              <fieldset>
                <legend>Please, enter your email, password and password confirmation for sign up.</legend>
                <div className="input-block">
                  <label>First Name</label>
                  <input id="signup-firstname" type="text"  name="firstName" onChange={this.handleChange} required/>
                </div>
                <div className="input-block">
                  <label>Last Name</label>
                  <input id="signup-lastname" type="text"  name="lastName" onChange={this.handleChange} required/>
                </div>
                <div className="input-block">
                  <label>E-mail</label>
                  <input id="signup-email" type="email"  name="email" onChange={this.handleChange} required/>
                </div>
                <div className="input-block">
                  <label>Password</label>
                  <input id="signup-password" type="password" name="password" onChange={this.handleChange} required/>
                  <span toggle="#password-field" name="signup-password-view" className="fa fa-fw fa-eye field-icon toggle-confirm-password" onClick={this.togglePassword}></span>
                </div>
                <div className="input-block">
                  <label>Confirm password</label>
                  <input id="signup-password-confirm" type="password" name="confirmPwd" onChange={this.handleChange} required/>
                </div>
              </fieldset>
              <button type="submit" className="btn-signup">Register</button>
            </form>
          </div>
        </div>
      </section>
      )
    }
  }


  
export default SignInUp
export const InfosEntrepreneur = React.createContext(this.state.dataEntrepreneur);
