var init;
class home extends Component {
  constructor(props) {
    super(props);

    this.state = {
      profil: "visiteur",
      dataInit: []
    }
    this.handleChange = this.handleSelectedChange.bind(this);
  }
  
    handleCardClick = index => {
      console.log(index, this);
    }
    handleEntrepreneurClick = index => {
      console.log(index, this);
    }
    handleSelectedChange(event) {
      console.log(event.target.value);
    }
    allResults() {
      axios.get ('/words?ml='+this.state.value).then(res => {
        result = res.data[Math.floor(Math.random() * (res.data.length - 0 + 1)) + 0].word.split("");
        console.log(result);
        while (i < result.length) {
          objIndexLetter.push(result[i]);
          i++;
        } 
        global = objIndexLetter;
        
        //this.setState({ finalValue: [result.join('')]});
        this.setState({ words: result});
  
      })
      axios.get ("http://localhost:5000/").then(res => {
        this.setState({dataInit: res}) 
      });
    }
    
    render() {
      const {profil, dataInit} = this.state
      return (
        <div className="App">
          <Headers profil={profil} onClickLogin={this.onLoginClick} onClickSignUp={this.onSignUpClick}/>
          <Body profil={profil} project={dataInit} onClickAllResults={this.allResults} onChangeLiked={this.handleSelectedChange} onClick={this.handleCardClick} onClick1={this.handleEntrepreneurClick}/>
        </div>
      )
    }
  }

export default home;
