import React, { Component } from 'react'
import axios from "axios";
var tabFiles = [];
class Entrepreneur extends Component {
  constructor(props) {
    super(props);
    this.state = {
      profil: "visiteur",
      selectedFile: []
    }
    this.handleChange = this.handleChange.bind(this);
    this.handleSubmit = this.handleSubmit.bind(this);
    this.fileSelectedhandler = this.fileSelectedhandler.bind(this);
  }
  handleChange(event) {
    this.setState({
      [event.target.name]: event.target.value
    });
  }
  handleSubmit(event) {
    const {nom, entrepreneur, financement, deadline, image, image_principale, images, video, description, social_network} = this.state;
    const fd = new FormData();
    fd.append("image",this.state.selectedFile, this.state.selectedFile.name);
    console.log(fd);
    axios.post("http://localhost:3000/project",{
      nom: nom,
      entrepreneur: entrepreneur,
      financement: financement,
      deadline: deadline,
      image: image,
      image_principale: image_principale,
      images: images,
      video: video,
      description: description,
      social_network: social_network
     })
    .then(response => {
      if(!response.data.err){
        if (response.data) {
          console.log(response);
          //this.props.handleSuccessfulAuth(response.data);
        }
      }else{
        alert(response.data.err);
      }

    })
    .catch(error => {
      console.log("login error", error);
    });
    event.preventDefault();
  }

  fileSelectedhandler(event) {
    tabFiles.push(event.target.files[0]);
    this.setState({
      selectedFile: tabFiles
    })
  }
  fileUploadhandler(event) {
    const fd = new FormData();
    console.log(fd + " " +event.selectedFile);
  }
  render (){
    return(
      <div id="idInterfaceEntrepreneur" className="form-wrapper">
      <form className="form"  onSubmit={this.handleSubmit} action="" method="POST">
        <div className="interface-entrepreneur">
          <div className="input">
            <label>Photo entrepreneur</label>
            <input id="entrepreneur-picture" type="file" name="entrepreneur-picture" onChange={this.fileSelectedhandler} required/>
            {/*<button onClick={this.fileUploadhandler}>Upload</button>*/}
          </div>
          <div className="input">
            <label>Photo principale du projet</label>
            <input id="project-picture" type="file" name="project-picture" onChange={this.fileSelectedhandler} required/>
          </div>
          <div className="input">
            <label>Photos du projet</label>
            <input id="project-pictures" type="file" name="project-pictures" onChange={this.fileSelectedhandler} required/>
          </div>
          <div className="input">
            <label>Video de présentation du projet</label>
            <input id="project-video" type="file" name="project-video" onChange={this.fileSelectedhandler} required/>
          </div>
          <div className="input">
            <label>Descriptif du projet</label>
            <textarea id="project-description" type="text" name="project-description" onChange={this.handleChange} required/>
          </div>
          <div className="input">
            <label>Timeline/planning</label>
            <input id="project-planning" type="text" name="project-planning" onChange={this.handleChange} required/>
          </div>
          <div className="input">
            <label>Besoins/Financement</label>
            <input id="project-funding" type="text" name="project-funding" onChange={this.handleChange} required/>
          </div>
          <div className="input">
            <label>Réseaux sociaux de suivi du projet</label>
            <input id="project-social-media" type="text" name="project-social-media" onChange={this.handleChange} required/>
          </div>
        </div>
        <button type="submit" className="btn-submit">
         Valider
        </button>
        <button type="submit" className="btn-submit">
         Annuler
        </button>
      </form>
    </div>
    )
 
  }
}




export default Entrepreneur
