import PropTypes from 'prop-types'
import React, { Component } from 'react'
import './Body.css'

var slideIndex = 1;
showSlides(slideIndex);

function plusSlides(n) {
  showSlides(slideIndex += n);
}

function currentSlide(n) {
  showSlides(slideIndex = n);
}

function showSlides(n) {
  var i;
  var slides = document.getElementsByClassName("mySlides");
  var dots = document.getElementsByClassName("dot");
  if (n > slides.length) {slideIndex = 1}    
  if (n < 1) {slideIndex = slides.length}
  for (i = 0; i < slides.length; i++) {
      slides[i].style.display = "none";  
  }
  for (i = 0; i < dots.length; i++) {
      dots[i].className = dots[i].className.replace(" active", "");
  }
  slides[slideIndex-1].style.display = "block";  
  dots[slideIndex-1].className += " active";
}


const ProjectBody = ({profil, project, index, onClick}) => (
    <div className="mySlides fade projectBodyContainer" onClick={() => onClick(index)}>
      <div className="numbertext">... / 3</div>
      <img className="imgProjectVis" src={`http://localhost/project/entrepreneurProject/zeproject/zefrontend/img/`+project.image+`.jpg`} alt="test" />
      {profil === "visiteur" ? 
        <div>
        <div className={`project ${project.title} ${project.person}`} >
          <br/>
          <span className="descriptionProject">
            <em>Nom du Projet</em><br/>
            <em>Nom de l'Entrepreneur</em><br/>
            <em>Financement</em><br/>
            <em>Deadline</em>
          </span>
        </div>
        <a className="prev" onclick="plusSlides(-1)">&#10094;</a>
        <a className="next" onclick="plusSlides(1)">&#10095;</a>
        </div>
        :
        <div>En cours</div>
      }
    </div>
  )



