import React, { Component } from 'react'
import axios from "axios";


var tabFiles = [];
class Entrepreneur extends Component {
  constructor(props) {
    super(props);
    this.state = {
      profil: "visiteur",
      infosEntrepreneur: [],
      selectedFile: []
    }
    this.handleChange = this.handleChange.bind(this);
    this.handleSubmit = this.handleSubmit.bind(this);
    this.fileSelectedhandler = this.fileSelectedhandler.bind(this);
  }
  handleChange(event) {
    this.setState({
      [event.target.name]: event.target.value
    });
  }
  handleSubmit(event) {
    const {infosEntrepreneur, project_name, project_description, project_funding, project_planning, project_social_media, selectedFile} = this.state;
    const fd = new FormData();
    fd.append("image",this.state.selectedFile[0], this.state.selectedFile[0].name);
    console.log(fd);
    axios.post("http://localhost:3000/project",{
      nom: project_name,
      entrepreneur: infosEntrepreneur.prenom + " " + infosEntrepreneur.nom,
      financement: project_funding,
      deadline: project_planning,
      image: selectedFile[0],
      image_principale: selectedFile[1],
      images: selectedFile[2],
      video: selectedFile[3],
      description: project_description,
      social_network: project_social_media
     })
    .then(response => {
      if(!response.data.err){
        if (response.data) {
          tabFiles = [];
          this.setState({
            selectedFile: []
          })
          console.log(response);
        }
      }else{
        alert(response.data.err);
      }

    })
    .catch(error => {
      console.log("login error", error);
    });
    event.preventDefault();
  }

  fileSelectedhandler(event) {
    tabFiles.push(event.target.files[0]);
    this.setState({
      selectedFile: tabFiles
    })
  }
  fileUploadhandler(event) {
    const fd = new FormData();
    console.log(fd + " " +event.selectedFile);
  }
  render (){
    <MyContext.Consumer>
        {context => (
                <h4>{context.dataEntrepreneur.nom}</h4>
        )}
    </MyContext.Consumer>
    return(
      <div id="idInterfaceEntrepreneur" className="form-wrapper">
      <form className="form"  onSubmit={this.handleSubmit} action="" method="POST">
        <div className="interface-entrepreneur">
          <div className="input-block">
              <label>Nom du projet</label>
              <input id="project_name" type="text"  name="project_name" onChange={this.handleChange} required/>
          </div>
          <div className="input">
            <label>Photo entrepreneur</label>
            <input id="entrepreneur-picture" type="file" name="entrepreneur-picture" onChange={this.fileSelectedhandler} required/>
          </div>
          <div className="input">
            <label>Photo principale du projet</label>
            <input id="project-picture" type="file" name="project-picture" onChange={this.fileSelectedhandler} required/>
          </div>
          <div className="input">
            <label>Photos du projet</label>
            <input id="project-pictures" type="file" name="project-pictures" onChange={this.fileSelectedhandler} required/>
          </div>
          <div className="input">
            <label>Video de présentation du projet</label>
            <input id="project-video" type="file" name="project-video" onChange={this.fileSelectedhandler} required/>
          </div>
          <div className="input">
            <label>Descriptif du projet</label>
            <textarea id="project_description" type="text" name="project_description" onChange={this.handleChange} required/>
          </div>
          <div className="input">
            <label>Timeline/planning</label>
            <input id="project_planning" type="text" name="project_planning" onChange={this.handleChange} required/>
          </div>
          <div className="input">
            <label>Besoins/Financement</label>
            <input id="project_funding" type="text" name="project_funding" onChange={this.handleChange} required/>
          </div>
          <div className="input">
            <label>Réseaux sociaux de suivi du projet</label>
            <input id="project_social_media" type="text" name="project_social_media" onChange={this.handleChange} required/>
          </div>
        </div>
        <button type="submit" className="btn-submit">
         Valider
        </button>
        <button type="submit" className="btn-submit">
         Annuler
        </button>
      </form>
    </div>
    )
 
  }
}




export default Entrepreneur
