import React, { Component } from 'react'
import './Entrepreneur.css'
import Headers from './Headers'
import Footers from './Footers'
import './Footers.css'
import axios from "axios";


var tabFiles = [], tabDates = [], tabLivrable = [], infosEntrepreneur;
class Entrepreneur extends Component {
  constructor(props) {
    super(props);
    this.state = {
      profil: "visiteur",
      planningDate: [],
      selectedFile: []
    }
    this.handleChange = this.handleChange.bind(this);
    this.handleSubmit = this.handleSubmit.bind(this);
    this.handleSubmitTimeline = this.handleSubmitTimeline.bind(this);
    this.fileSelectedhandler = this.fileSelectedhandler.bind(this);
  }
  handleChange(event) {
    this.setState({
      [event.target.name]: event.target.value
    });
  }
  handleSubmit(event) {
    const {project_name, project_description, project_funding, project_planning, project_social_media, selectedFile} = this.state;
    const fd = new FormData();
    fd.append("image",this.state.selectedFile[0], this.state.selectedFile[0].name);
    console.log(fd);
    axios.post("http://localhost:3000/project",{
      nom: project_name,
      entrepreneur: infosEntrepreneur[0].prenom + " " + infosEntrepreneur[0].nom,
      financement: project_funding,
      deadline: project_planning,
      image: selectedFile[0].name,
      image_principale: selectedFile[1].name,
      images: selectedFile[2].name,
      video: selectedFile[3].name,
      description: project_description,
      social_network: project_social_media
     })
    .then(response => {
      if(!response.data.err){
        if (response.data) {
          tabFiles = [];
          this.setState({
            selectedFile: []
          })
          console.log(response);
        }
      }else{
        alert(response.data.err);
      }

    })
    .catch(error => {
      console.log("login error", error);
    });
    event.preventDefault();
  }
  handleSubmitTimeline(event) {
    const {project_name, planningDate, livrable} = this.state;
    axios.post("http://localhost:3000/planning",{
      project: project_name,
      entrepreneur: infosEntrepreneur[0].prenom + " " + infosEntrepreneur[0].nom,
      date: planningDate,
      livrable: livrable
     })
    .then(response => {
      if(!response.data.err){
        if (response.data) {
          tabFiles = [];
          this.setState({
            selectedFile: []
          })
          console.log(response);
        }
      }else{
        alert(response.data.err);
      }

    })
    .catch(error => {
      console.log("login error", error);
    });
    event.preventDefault();
  }
  dateSelectedhandler(event) {
    tabDates.push(event.target.value[0]);
    this.setState({
      planningDate: tabDates
    })
  }
  fileSelectedhandler(event) {
    tabFiles.push(event.target.files[0]);
    this.setState({
      selectedFile: tabFiles
    })
  }
  fileUploadhandler(event) {
    const fd = new FormData();
    console.log(fd + " " +event.selectedFile);
  }
  render (){
    infosEntrepreneur = this.props.history.location.state.infos;
    return(
      <div>
      <Headers profil={"visiteur"} where={"nothome"}/>
      <div className="choice-buttons">
        <a className="button border pill" href="#">Vue créateur</a>
        <a className="button box-shadow pill" href="#">Vue visiteur</a>
      </div>
      <div id="idInterfaceEntrepreneur" className="container-entrepreneur form-wrapper">
      <form className=""  onSubmit={this.handleSubmit} action="" method="POST">
        <div className="interface-entrepreneur">
          <div className="group">
              <input id="project_name" type="text"  name="project_name" onChange={this.handleChange} required/>
              <span className="highlight"></span>
              <span className="bar"></span>
              <label className="form__label">Nom du projet </label>
          </div>

          <div className="input">
            <div className="label-div">Photo entrepreneur </div>
            <input id="entrepreneur-picture" className="input-file" type="file" name="entrepreneur-picture" onChange={this.fileSelectedhandler} required/>
          </div>
          <div className="input">
            <div className="label-div">Photo principale du projet </div>
            <input id="project-picture" className="input-file" type="file" name="project-picture" onChange={this.fileSelectedhandler} required/>
          </div>
          <div className="input">
            <div className="label-div">Photos du projet </div>
            <input id="project-pictures" className="input-file" type="file" name="project-pictures" onChange={this.fileSelectedhandler} required/>
          </div>
          <div className="input">
            <div className="label-div">Video de présentation du projet </div>
            <input id="project-video" className="input-file" type="file" name="project-video" onChange={this.fileSelectedhandler} required/>
          </div>
          <div className="input">
            <div className="label-div">Descriptif du projet </div>
            <textarea id="project_description" placeholder="" type="text" name="project_description" onChange={this.handleChange} required/>
          </div>
          {/*<div className="group">
              <input id="project_planning" type="text" name="project_planning" onChange={this.handleChange} required/>
              <span className="highlight"></span>
              <span className="bar"></span>
              <label>Timeline/planning </label>
          </div>*/}
          <div className="group-timeline" onSubmit={this.handleSubmitTimeline} action="" method="POST">
              <div className="label-div" id="timeline">Timeline</div>
              <div className="group">
                <div className="label-div">Date prévisionnelle:</div>
                <input type="date" id="datPrev" name="datPrev" onChange={this.dateSelectedhandler} required/>
                <div className="label-div">Livrables:</div>
                <input type="text" id="livrable" name="livrable" onChange={this.handleChange} required/>
                <input className="btnSendDate" type="submit"/>
              </div>
          </div>

          <div className="group">
              <input id="project_funding" type="text" name="project_funding" onChange={this.handleChange} required/>
              <span className="highlight"></span>
              <span className="bar"></span>
              <label>Besoins/Financement </label>
          </div>
          <div className="group">
              <input id="project_social_media" type="text" name="project_social_media" onChange={this.handleChange} required/>
              <span className="highlight"></span>
              <span className="bar"></span>
              <label>Réseaux sociaux de suivi du projet </label>
          </div>
        </div>
        <div className="bar-valid">
        <button id="btn-valid-project"type="submit" className="btn btn-success">
         Valider
        </button>
        <button className="btn btn-danger">
         Annuler
        </button>
        </div>
      </form>
    </div>
    <Footers profil={"visiteur"}/>
    </div>

    )
 
  }
}




export default Entrepreneur
