import PropTypes from 'prop-types'
import './SignInUp.css'
import Headers from './Headers'
import axios from "axios";
import { Link } from 'react-router-dom';
import React, { Component } from 'react'

class SignInUp extends Component {
  constructor(props) {
    super(props);
    this.state = {
      profil: "visiteur",
      firstName: "",
      lastName: "",
      email: "",
      password: "",
      confirmPwd: "",
      loginErrors: "",
      dataEntrepreneur: []
    }
    this.handleLogin = this.handleLogin.bind(this);
    this.handleSignUp = this.handleSignUp.bind(this);
    this.handleChange = this.handleChange.bind(this);
  }
  handleChangePanelLogin(event) {
    document.getElementById('idPanelLogin').classList.add("is-active");
    document.getElementById('idPanelSignUp').classList.remove("is-active");
  }
  handleChangePanelSignUp(event) {
    document.getElementById('idPanelSignUp').classList.add("is-active");
    document.getElementById('idPanelLogin').classList.remove("is-active");
  }
  handleChange(event) {
    this.setState({
      [event.target.name]: event.target.value
    });
  }
  checkPassword(confirmPwd) {
    const {password} = this.state;
    var decimal=  /^(?=.*\d)(?=.*[a-z])(?=.*[A-Z])(?=.*[^a-zA-Z0-9])(?!.*\s).{8,15}$/;
    if(!password.match(decimal)) { 
        alert('Wrong...!')
        return;
      }
    } 
  handleLogin(event) {
    const { email, password } = this.state;
    axios.post("http://localhost:3000/login",{
            email: email,
            password: password
        },
        { withCredentials: true }
      )
      .then(response => {
        if (response.data) {
          console.log(response);
          //this.props.handleSuccessfulAuth(response.data);
        }
      })
      .catch(error => {
        console.log("login error", error);
      });
    event.preventDefault();
  }
  handleSignUp(event) {
    const { firstName, lastName, email, password, confirmPwd } = this.state;
    if(checkPassword(confirmPwd)){
      axios.post("http://localhost:3000/register",{
        firstName: firstName,
        lastName: lastName,
        email: email,
        password: password
    },
    { withCredentials: true }
    )
    .then(response => {
      if (response.data) {
        console.log(response);
        //this.props.handleSuccessfulAuth(response.data);
      }
    })
    .catch(error => {
      console.log("login error", error);
    });
  }

    event.preventDefault();
  }



    render() {
      const {profil, dataEntrepreneur} = this.state
      return (
        <section className="forms-section" id="containerLogSign">
        <Headers profil={profil} onClickLogin={this.onLoginClick} onClickSignUp={this.onSignUpClick} where={"siu"}/>
        <h1 className="section-title"></h1>
        <div className="forms" >
          <div id="idPanelLogin" className="form-wrapper is-active">
            <button type="button"  className="switcher switcher-login" onClick={this.handleChangePanelLogin}>
              Login
              <span className="underline"></span>
            </button>
            <form className="form form-login"  onSubmit={this.handleLogin} action="entrepreneur" method="POST">
              <fieldset>
                <legend>Please, enter your email and password for login.</legend>
                <div className="input-block">
                  <label>E-mail</label>
                  <input id="login-email" type="email" name="email" onChange={this.handleChange} required/>
                </div>
                <div className="input-block">
                  <label>Password</label>
                  <input id="login-password" type="password" name="password" onChange={this.handleChange} required/>
                </div>
              </fieldset>
              <button type="submit" className="btn-login">
              Login
              {/*<Entrepreneur dataEntrepreneur={dataEntrepreneur}></Entrepreneur> <Link to="/entrepreneur">Login</Link>*/}
              </button>
            </form>
          </div>
          <div id="idPanelSignUp" className="form-wrapper">
            <button type="button"  className="switcher switcher-signup right-panel-active" onClick={this.handleChangePanelSignUp}>
              Sign Up
              <span className="underline"></span>
            </button>
            <form className="form form-signup"  onSubmit={this.handleSignUp} action="entrepreneur" method="POST">
              <fieldset>
                <legend>Please, enter your email, password and password confirmation for sign up.</legend>
                <div className="input-block">
                  <label>First Name</label>
                  <input id="signup-firstname" type="text"  name="firstName" onChange={this.handleChange} required/>
                </div>
                <div className="input-block">
                  <label>Last Name</label>
                  <input id="signup-lastname" type="text"  name="lastName" onChange={this.handleChange} required/>
                </div>
                <div className="input-block">
                  <label>E-mail</label>
                  <input id="signup-email" type="email"  name="email" onChange={this.handleChange} required/>
                </div>
                <div className="input-block">
                  <label>Password</label>
                  <input id="signup-password" type="password" name="password" onChange={this.handleChange} required/>
                </div>
                <div className="input-block">
                  <label>Confirm password</label>
                  <input id="signup-password-confirm" type="password" name="confirmPwd" onChange={this.handleChange} required/>
                </div>
              </fieldset>
              <button type="submit" className="btn-signup">Register</button>
            </form>
          </div>
        </div>
      </section>
      )
    }
  }


  
export default SignInUp
